import React, { useState } from 'react';
import { Eye, EyeOff, AlertCircle, CheckCircle } from 'lucide-react';
import { useAppContext } from '../context/AppContext';

const Homepage = ({ onLogin, selectedRole: initialRole, onBack }) => {
  const { registerUser, authenticateUser } = useAppContext();
  const [isLogin, setIsLogin] = useState(true);
  const [selectedRole, setSelectedRole] = useState(initialRole || 'trainer');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [errors, setErrors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    empId: '',
    trainerEmpId: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (errors.length > 0) {
      setErrors([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors([]);

    try {
      if (isLogin) {
        const user = authenticateUser(formData.email, formData.password);
        onLogin(user);
      } else {
        if (formData.password !== formData.confirmPassword) {
          throw new Error('Passwords do not match!');
        }

        if (!formData.empId) {
          throw new Error('Employee ID is required');
        }

        if (selectedRole === 'trainee' && !formData.trainerEmpId) {
          throw new Error('Trainer Employee ID is required for trainees');
        }

        const userData = {
          name: formData.name,
          email: formData.email,
          password: formData.password,
          role: selectedRole,
          empId: formData.empId,
          skills: 'JavaScript, React, Node.js', // Default skills
          trainerEmpId: selectedRole === 'trainee' ? formData.trainerEmpId : null
        };

        const result = registerUser(userData);

        if (result.requestPending) {
          alert('Registration request submitted! Please wait for trainer approval before logging in.');
          setFormData({
            name: '',
            email: '',
            password: '',
            confirmPassword: '',
            empId: '',
            trainerEmpId: ''
          });
        } else {
          alert('Registration successful!');
          if (selectedRole === 'trainer') {
            onLogin(result);
          }
        }
      }
    } catch (error) {
      setErrors([error.message]);
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
      empId: '',
      trainerEmpId: ''
    });
  };

  return (
    <div className="homepage">
      <div className="homepage-container">
        <div className="homepage-left">
          <button className="back-btn" onClick={onBack}>
            ‚Üê Back to Role Selection
          </button>
          <div className="logo-container">
            <div className="logo">
              <div className="logo-icon">
                <img 
                  src="https://www.companieslogo.com/img/orig/CTSH-82a8444b.png" 
                  alt="Cognizant Logo"
                />
              </div>
              <div className="logo-name">
                <h1>Trainee Management Portal</h1>
                <p>Program</p>
              </div>
            </div>
          </div>
          <p className="homepage-subtitle">
            Revolutionize your learning experience with our cutting-edge platform. 
            Seamlessly connect trainers and trainees, manage comprehensive coursework, 
            track real-time progress, and unlock your full potential.
          </p>
          <div className="features-highlight">
            <div className="feature-point">
              <span className="feature-icon">üéØ</span>
              <span>Smart Learning Paths</span>
            </div>
            <div className="feature-point">
              <span className="feature-icon">üìä</span>
              <span>Real-time Analytics</span>
            </div>
            <div className="feature-point">
              <span className="feature-icon">üöÄ</span>
              <span>Career Acceleration</span>
            </div>
          </div>
        </div>

        <div className="homepage-right">
          <div className="auth-container">
            <div className="role-display">
              <h2>Welcome {selectedRole === 'trainer' ? 'üë®üè´ Trainer' : 'üë®üéì Trainee'}</h2>
              <p>Please login or sign up to continue</p>
            </div>

            <div className="auth-tabs">
              <button 
                className={`auth-tab ${isLogin ? 'active' : ''}`}
                onClick={() => {
                  setIsLogin(true);
                  setErrors([]);
                  resetForm();
                }}
              >
                Login
              </button>
              <button 
                className={`auth-tab ${!isLogin ? 'active' : ''}`}
                onClick={() => {
                  setIsLogin(false);
                  setErrors([]);
                  resetForm();
                }}
              >
                Sign Up
              </button>
            </div>

            {errors.length > 0 && (
              <div className="error-container">
                <AlertCircle size={16} className="error-icon" />
                <div className="error-messages">
                  {errors.map((error, index) => (
                    <div key={index} className="error-message">{error}</div>
                  ))}
                </div>
              </div>
            )}

            <form className="auth-form" onSubmit={handleSubmit}>
              {!isLogin && (
                <div className="form-group">
                  <label>Full Name *</label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your full name"
                    className="form-input"
                  />
                </div>
              )}

              <div className="form-group">
                <label>Email *</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                  placeholder="Enter your email"
                  className="form-input"
                />
              </div>

              <div className="form-group">
                <label>Password *</label>
                <div className="password-input-container">
                  <input
                    type={showPassword ? 'text' : 'password'}
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your password"
                    className="form-input password-input"
                  />
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? <EyeOff size={16} /> : <Eye size={16} />}
                  </button>
                </div>
              </div>

              {!isLogin && (
                <div className="form-group">
                  <label>Confirm Password *</label>
                  <div className="password-input-container">
                    <input
                      type={showConfirmPassword ? 'text' : 'password'}
                      name="confirmPassword"
                      value={formData.confirmPassword}
                      onChange={handleInputChange}
                      required
                      placeholder="Confirm your password"
                      className="form-input password-input"
                    />
                    <button
                      type="button"
                      className="password-toggle"
                      onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    >
                      {showConfirmPassword ? <EyeOff size={16} /> : <Eye size={16} />}
                    </button>
                  </div>
                </div>
              )}

              {!isLogin && (
                <div className="form-group">
                  <label>Employee ID *</label>
                  <input
                    type="text"
                    name="empId"
                    value={formData.empId}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your employee ID"
                    className="form-input"
                  />
                </div>
              )}

              {!isLogin && selectedRole === 'trainee' && (
                <div className="form-group">
                  <label>Trainer Employee ID *</label>
                  <input
                    type="text"
                    name="trainerEmpId"
                    value={formData.trainerEmpId}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter trainer's employee ID"
                    className="form-input"
                  />
                </div>
              )}

              <button type="submit" className="auth-btn" disabled={loading}>
                {loading ? (
                  <div className="loading-spinner"></div>
                ) : (
                  `${isLogin ? 'Login' : 'Sign Up'} as ${selectedRole}`
                )}
              </button>
            </form>
          </div>
        </div>
      </div>

